#include "CompHandler.hh"
#include "gen/SmartIDSuEyeImageServer.hh"

#include <iostream>

// include communication objects
#include <CommBasicObjects/commBaseState.hh>

#include <CommBasicObjects/commDevicePoseState.hh>

#include <CommBasicObjects/commVoid.hh>

#include <CommVisionObjects/commVideoImage.hh>

void CompHandler::onStartup()
{

	CHS::StatusCode status;

	// connect to all services
	if (COMP->ini.base.on_base)
	{
		std::cout << "connecting to: " << COMP->ini.basePushTimedClient.serverName << "; " << COMP->ini.basePushTimedClient.serviceName
				<< std::endl;
		status = COMP->basePushTimedClient->connect(COMP->ini.basePushTimedClient.serverName, COMP->ini.basePushTimedClient.serviceName);
		while (status != CHS::SMART_OK)
		{
			usleep(500000);
			status
					= COMP->basePushTimedClient->connect(COMP->ini.basePushTimedClient.serverName,
							COMP->ini.basePushTimedClient.serviceName);
		}
		std::cout << "connected.\n";

		COMP->basePushTimedClient->subscribe(COMP->ini.basePushTimedClient.interval);
	}

	if (COMP->ini.base.on_actuator)
	{
		std::cout << "connecting to: " << COMP->ini.actuatorPushTimedClient.serverName << "; "
				<< COMP->ini.actuatorPushTimedClient.serviceName << std::endl;
		status = COMP->actuatorPushTimedClient->connect(COMP->ini.actuatorPushTimedClient.serverName,
				COMP->ini.actuatorPushTimedClient.serviceName);
		while (status != CHS::SMART_OK)
		{
			usleep(500000);
			status = COMP->actuatorPushTimedClient->connect(COMP->ini.actuatorPushTimedClient.serverName,
					COMP->ini.actuatorPushTimedClient.serviceName);
		}
		std::cout << "connected.\n";

		COMP->actuatorPushTimedClient->subscribe();
	}

	if (COMP->ini.base.on_manipulator)
	{
		std::cout << "connecting to: " << COMP->ini.manipulatorPushTimedClient.serverName << "; "
				<< COMP->ini.manipulatorPushTimedClient.serviceName << std::endl;
		status = COMP->manipulatorPushTimedClient->connect(COMP->ini.manipulatorPushTimedClient.serverName,
				COMP->ini.manipulatorPushTimedClient.serviceName);
		while (status != CHS::SMART_OK)
		{
			usleep(500000);
			status = COMP->manipulatorPushTimedClient->connect(COMP->ini.manipulatorPushTimedClient.serverName,
					COMP->ini.manipulatorPushTimedClient.serviceName);
		}
		std::cout << "connected.\n";
	}

	// activate state server
	if (COMP->stateServer->activate() != CHS::SMART_OK)
		std::cerr << "ERROR: activate state" << std::endl;

	// set camera parameters
	COMP->camera.setDebug(COMP->ini.settings.debug_info);
	COMP->camera.undistortImage(COMP->ini.settings.undistort_image);
	COMP->camera.setCameraParmas(COMP->ini.hardware_properties.intrinsicFx, COMP->ini.hardware_properties.intrinsicFy,
			COMP->ini.hardware_properties.intrinsicCx, COMP->ini.hardware_properties.intrinsicCy,
			COMP->ini.hardware_properties.distortionK1, COMP->ini.hardware_properties.distortionK2,
			COMP->ini.hardware_properties.distortionP1, COMP->ini.hardware_properties.distortionP2,
			COMP->ini.hardware_properties.distortionK3);

	// run all tasks
	COMP->imageTask.open();
}
