#include "CompHandler.hh"
#include "gen/SmartKatana.hh"

#include <iostream>

// include communication objects
#include "externalCommObjectHeaders.hh"
#include <commManipulatorParameter.hh>
#include <commManipulatorTrajectory.hh>
#include <commMobileManipulatorState.hh>

#include <commBaseState.hh>

// other includes
#include "Katana.hh"

void CompHandler::onStartup()
{
	std::cout << "Component is starting ...\n";

	Smart::CommBaseState dummy;

	////////////////////////////
	// Startup Code for Katana
	////////////////////////////

	KATANA->init();

	//////////////////////
	// Other startup code
	//////////////////////
	CHS::StatusCode status;

	// activate states
	COMP->stateServer->activate();

	// connect to baseServer if it is activated in the ini file
	if (COMP->ini.Katana.BaseInit)
	{
		std::cout << "connecting to: "
				<< COMP->ini.basePushTimedClient.serverName << "; "
				<< COMP->ini.basePushTimedClient.serviceName << std::endl;
		status = COMP->basePushTimedClient->connect(
				COMP->ini.basePushTimedClient.serverName,
				COMP->ini.basePushTimedClient.serviceName);
		while (status != CHS::SMART_OK)
		{
			usleep(500000);
			status = COMP->basePushTimedClient->connect(
					COMP->ini.basePushTimedClient.serverName,
					COMP->ini.basePushTimedClient.serviceName);
		}
		std::cout << "connected.\n";

		COMP->basePushTimedClient->subscribe(
				COMP->ini.basePushTimedClient.interval);
	}

	// start push timed server
	COMP->posePushTimedServer->start();

	std::cout << "Component started ...\n";
}
