#include "CompHandler.hh"
#include "gen/SmartLaserObstacleAvoid.hh"

#include <iostream>

// include communication objects
#include <CommBasicObjects/commMobileLaserScan.hh>
#include <CommBasicObjects/commNavigationVelocity.hh>

void CompHandler::onStartup()
{
	std::cout
			<< "startup - put your startupCode in CompHandler::onStartup()!!!\n";

	CHS::StatusCode status;

	// connect to all services

	std::cout << "connecting to: "
			<< COMP->ini.navigationVelocitySendClient.serverName << "; "
			<< COMP->ini.navigationVelocitySendClient.serviceName << std::endl;
	status = COMP->navigationVelocitySendClient->connect(
			COMP->ini.navigationVelocitySendClient.serverName,
			COMP->ini.navigationVelocitySendClient.serviceName);
	while (status != CHS::SMART_OK)
	{
		usleep(500000);
		status = COMP->navigationVelocitySendClient->connect(
				COMP->ini.navigationVelocitySendClient.serverName,
				COMP->ini.navigationVelocitySendClient.serviceName);
	}
	std::cout << "connected.\n";

	std::cout << "connecting to: " << COMP->ini.laserClient.serverName << "; "
			<< COMP->ini.laserClient.serviceName << std::endl;
	status = COMP->laserClient->connect(COMP->ini.laserClient.serverName,
			COMP->ini.laserClient.serviceName);
	while (status != CHS::SMART_OK)
	{
		usleep(500000);
		status = COMP->laserClient->connect(COMP->ini.laserClient.serverName,
				COMP->ini.laserClient.serviceName);
	}
	std::cout << "connected.\n";

	COMP->laserClient->subscribe();

	// run all tasks
	COMP->robotTask.open();

}
