#include "CompHandler.hh"
#include "gen/SmartPlannerBreadthFirstSearch.hh"

#include <iostream>

// include communication objects

void CompHandler::onStartup()
{
	CHS::StatusCode status;
	int status2;

    status2 = goalFifoInit(&(COMP->goalFifoHead), &(COMP->goalFifoTail) );

	// connect to all services
	std::cout << "connecting to: " << COMP->ini.curMapClient.serverName << "; "
			<< COMP->ini.curMapClient.serviceName << std::endl;
	status = COMP->curMapClient->connect(COMP->ini.curMapClient.serverName,
			COMP->ini.curMapClient.serviceName);
	while (status != CHS::SMART_OK)
	{
		usleep(500000);
		status = COMP->curMapClient->connect(COMP->ini.curMapClient.serverName,
				COMP->ini.curMapClient.serviceName);
	}
	std::cout << "connected.\n";

	COMP->curMapClient->subscribe();
	std::cout << "connecting to: " << COMP->ini.baseClient.serverName << "; "
			<< COMP->ini.baseClient.serviceName << std::endl;
	status = COMP->baseClient->connect(COMP->ini.baseClient.serverName,
			COMP->ini.baseClient.serviceName);
	while (status != CHS::SMART_OK)
	{
		usleep(500000);
		status = COMP->baseClient->connect(COMP->ini.baseClient.serverName,
				COMP->ini.baseClient.serviceName);
	}
	std::cout << "connected.\n";

	COMP->baseClient->subscribe(COMP->ini.baseClient.interval);

	// activate state server
	if (COMP->stateServer->activate() != CHS::SMART_OK)
		std::cerr << "ERROR: activate state" << std::endl;

	// run all tasks
	COMP->plannerTask.open();
}
