#include "CompHandler.hh"
#include "gen/SmartTTSClient.hh"

#include <iostream>

// include communication objects
#include <CommBasicObjects/commPropertySet.hh>

#include <CommSpeechObjects/commSpeechOutputMessage.hh>

void CompHandler::onStartup()
{
	std::cout
			<< "startup - put your startupCode in CompHandler::onStartup()!!!\n";

	CHS::StatusCode status;

	// connect to all services
	std::cout << "connecting to: " << COMP->ini.queryClient.serverName << "; "
			<< COMP->ini.queryClient.serviceName << std::endl;
	status = COMP->queryClient->connect(COMP->ini.queryClient.serverName,
			COMP->ini.queryClient.serviceName);
	while (status != CHS::SMART_OK)
	{
		usleep(500000);
		status = COMP->queryClient->connect(COMP->ini.queryClient.serverName,
				COMP->ini.queryClient.serviceName);
	}
	std::cout << "connected.\n";

	std::cout << "connecting to: " << COMP->ini.sendClient.serverName << "; "
			<< COMP->ini.sendClient.serviceName << std::endl;
	status = COMP->sendClient->connect(COMP->ini.sendClient.serverName,
			COMP->ini.sendClient.serviceName);
	while (status != CHS::SMART_OK)
	{
		usleep(500000);
		status = COMP->sendClient->connect(COMP->ini.sendClient.serverName,
				COMP->ini.sendClient.serviceName);
	}
	std::cout << "connected.\n";

	// run all tasks
	COMP->consoleTask.open();

}
